import React, { useState } from "react";

function Login() {
  const [token, setToken] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");
  const [showInstructions, setShowInstructions] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      const response = await fetch("/api/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify({
          token: token.trim(),
        }),
      });

      if (response.ok) {
        // Login successful, reload to show authenticated app
        window.location.href = "/";
      } else {
        const data = await response.json();
        setError(data.detail || "Invalid token. Please check your Personal Access Token and try again.");
      }
    } catch (err) {
      setError("Login failed. Please check your connection and try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <div style={styles.logo}>
            <span style={styles.logoIcon}>‚ú®</span>
          </div>
          <h1 style={styles.title}>Table Enrichment</h1>
          <p style={styles.subtitle}>AI-Powered Metadata Enhancement</p>
        </div>

        <form onSubmit={handleSubmit} style={styles.form}>
          <div style={styles.content}>
            <p style={styles.description}>
              Sign in with your Databricks Personal Access Token to access the enrichment tool.
            </p>

            {error && (
              <div style={styles.errorBox}>
                <span style={styles.errorIcon}>‚ö†Ô∏è</span>
                <span style={styles.errorText}>{error}</span>
              </div>
            )}

            <div style={styles.inputGroup}>
              <label style={styles.label} htmlFor="token">
                Personal Access Token
              </label>
              <textarea
                id="token"
                value={token}
                onChange={(e) => setToken(e.target.value)}
                placeholder="dapi********************************"
                style={styles.textarea}
                rows={3}
                required
                disabled={isLoading}
              />
              <div style={styles.hint}>
                Token starts with "dapi" followed by alphanumeric characters
              </div>
            </div>

            <button
              type="submit"
              disabled={isLoading || !token.trim()}
              style={{
                ...styles.loginButton,
                ...(isLoading || !token.trim() ? styles.loginButtonDisabled : {}),
              }}
            >
              {isLoading ? (
                <>
                  <div style={styles.spinner}></div>
                  Validating...
                </>
              ) : (
                <>
                  <span style={styles.buttonIcon}>üîê</span>
                  Sign in to Databricks
                </>
              )}
            </button>

            <button
              type="button"
              onClick={() => setShowInstructions(!showInstructions)}
              style={styles.instructionsButton}
            >
              {showInstructions ? "Hide" : "How to get a token?"} 
              <span style={styles.arrow}>{showInstructions ? "‚ñ≤" : "‚ñº"}</span>
            </button>

            {showInstructions && (
              <div style={styles.instructions}>
                <h3 style={styles.instructionsTitle}>üìã Steps to Generate Token:</h3>
                <ol style={styles.instructionsList}>
                  <li>Open your Databricks workspace</li>
                  <li>Click your <strong>email/username</strong> (top-right corner)</li>
                  <li>Go to <strong>User Settings</strong> ‚Üí <strong>Developer</strong></li>
                  <li>Click <strong>Access tokens</strong></li>
                  <li>Click <strong>Generate new token</strong></li>
                  <li>Give it a name: "Table Enrichment App"</li>
                  <li>Set lifetime (optional) or leave blank</li>
                  <li>Click <strong>Generate</strong></li>
                  <li><strong>Copy the token immediately</strong> (shown only once!)</li>
                  <li>Paste it above and click Sign in</li>
                </ol>
                <div style={styles.warningBox}>
                  <span style={styles.warningIcon}>‚ö°</span>
                  <span style={styles.warningText}>
                    The token is shown only once. Store it securely!
                  </span>
                </div>
              </div>
            )}

            <div style={styles.features}>
              <div style={styles.feature}>
                <span style={styles.featureIcon}>ü§ñ</span>
                <span style={styles.featureText}>AI-generated descriptions</span>
              </div>
              <div style={styles.feature}>
                <span style={styles.featureIcon}>üìä</span>
                <span style={styles.featureText}>Table & column enrichment</span>
              </div>
              <div style={styles.feature}>
                <span style={styles.featureIcon}>üîí</span>
                <span style={styles.featureText}>Secure token authentication</span>
              </div>
            </div>
          </div>
        </form>

        <div style={styles.footer}>
          <p style={styles.footerText}>
            Powered by Databricks Foundation Models
          </p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    padding: '20px',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: '16px',
    boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
    maxWidth: '500px',
    width: '100%',
    overflow: 'hidden',
    maxHeight: '90vh',
    display: 'flex',
    flexDirection: 'column',
  },
  header: {
    padding: '48px 32px 32px',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
  },
  logo: {
    marginBottom: '16px',
  },
  logoIcon: {
    fontSize: '48px',
    display: 'inline-block',
    animation: 'float 3s ease-in-out infinite',
  },
  title: {
    margin: '0 0 8px 0',
    fontSize: '32px',
    fontWeight: '700',
  },
  subtitle: {
    margin: 0,
    fontSize: '16px',
    opacity: 0.9,
    fontWeight: '400',
  },
  form: {
    width: '100%',
    flex: 1,
    overflowY: 'auto',
  },
  content: {
    padding: '32px',
  },
  description: {
    margin: '0 0 24px 0',
    fontSize: '15px',
    lineHeight: '1.6',
    color: '#4b5563',
    textAlign: 'center',
  },
  errorBox: {
    display: 'flex',
    alignItems: 'flex-start',
    gap: '8px',
    padding: '12px 16px',
    backgroundColor: '#fef2f2',
    border: '1px solid #fecaca',
    borderRadius: '8px',
    marginBottom: '20px',
  },
  errorIcon: {
    fontSize: '18px',
    marginTop: '2px',
  },
  errorText: {
    fontSize: '14px',
    color: '#991b1b',
    flex: 1,
  },
  inputGroup: {
    marginBottom: '20px',
  },
  label: {
    display: 'block',
    fontSize: '14px',
    fontWeight: '500',
    color: '#374151',
    marginBottom: '8px',
  },
  textarea: {
    width: '100%',
    padding: '12px 16px',
    fontSize: '14px',
    border: '1px solid #d1d5db',
    borderRadius: '8px',
    outline: 'none',
    transition: 'border-color 0.2s',
    boxSizing: 'border-box',
    fontFamily: 'monospace',
    resize: 'vertical',
  },
  hint: {
    marginTop: '6px',
    fontSize: '12px',
    color: '#6b7280',
  },
  loginButton: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '12px',
    padding: '16px 24px',
    backgroundColor: '#667eea',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 12px rgba(102, 126, 234, 0.4)',
    marginBottom: '12px',
  },
  loginButtonDisabled: {
    opacity: 0.6,
    cursor: 'not-allowed',
  },
  buttonIcon: {
    display: 'flex',
    alignItems: 'center',
    fontSize: '18px',
  },
  spinner: {
    width: '16px',
    height: '16px',
    border: '2px solid rgba(255, 255, 255, 0.3)',
    borderTop: '2px solid white',
    borderRadius: '50%',
    animation: 'spin 1s linear infinite',
  },
  instructionsButton: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '8px',
    padding: '12px',
    backgroundColor: 'transparent',
    color: '#667eea',
    border: '1px solid #667eea',
    borderRadius: '8px',
    fontSize: '14px',
    fontWeight: '500',
    cursor: 'pointer',
    transition: 'all 0.2s',
    marginBottom: '20px',
  },
  arrow: {
    fontSize: '12px',
  },
  instructions: {
    padding: '20px',
    backgroundColor: '#f9fafb',
    borderRadius: '8px',
    marginBottom: '20px',
  },
  instructionsTitle: {
    margin: '0 0 16px 0',
    fontSize: '16px',
    fontWeight: '600',
    color: '#111827',
  },
  instructionsList: {
    margin: '0 0 16px 0',
    paddingLeft: '20px',
    fontSize: '14px',
    lineHeight: '1.8',
    color: '#374151',
  },
  warningBox: {
    display: 'flex',
    alignItems: 'center',
    gap: '8px',
    padding: '10px 12px',
    backgroundColor: '#fffbeb',
    border: '1px solid #fcd34d',
    borderRadius: '6px',
  },
  warningIcon: {
    fontSize: '16px',
  },
  warningText: {
    fontSize: '13px',
    color: '#92400e',
    fontWeight: '500',
  },
  features: {
    display: 'flex',
    flexDirection: 'column',
    gap: '12px',
  },
  feature: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    padding: '12px 16px',
    backgroundColor: '#f9fafb',
    borderRadius: '8px',
  },
  featureIcon: {
    fontSize: '24px',
  },
  featureText: {
    fontSize: '14px',
    color: '#374151',
    fontWeight: '500',
  },
  footer: {
    padding: '24px 32px',
    backgroundColor: '#f9fafb',
    borderTop: '1px solid #e5e7eb',
    textAlign: 'center',
  },
  footerText: {
    margin: 0,
    fontSize: '13px',
    color: '#6b7280',
  },
};

// Add CSS
const styleSheet = document.styleSheets[0];
const rules = `
  textarea:focus {
    border-color: #667eea !important;
    box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1) !important;
  }
  button[type="submit"]:not(:disabled):hover {
    background-color: #5a67d8 !important;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.5) !important;
  }
  button[type="button"]:hover {
    background-color: #eff6ff !important;
  }
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }
  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;
try {
  styleSheet.insertRule(rules, styleSheet.cssRules.length);
} catch (e) {}

export default Login;
