import React from "react";

function Login() {
  const handleLogin = () => {
    // Redirect to backend OAuth endpoint
    window.location.href = "/auth/login";
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <div style={styles.logo}>
            <span style={styles.logoIcon}>âœ¨</span>
          </div>
          <h1 style={styles.title}>Table Enrichment</h1>
          <p style={styles.subtitle}>AI-Powered Metadata Enhancement</p>
        </div>

        <div style={styles.content}>
          <p style={styles.description}>
            Sign in with your Databricks account to access the table and column description enrichment tool.
          </p>

          <button onClick={handleLogin} style={styles.loginButton}>
            <span style={styles.buttonIcon}>
              <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
                <path d="M12 2L2 7v10c0 5.55 3.84 10.74 9 12 5.16-1.26 9-6.45 9-12V7l-10-5z"/>
              </svg>
            </span>
            Sign in with Databricks
          </button>

          <div style={styles.features}>
            <div style={styles.feature}>
              <span style={styles.featureIcon}>ðŸ¤–</span>
              <span style={styles.featureText}>AI-generated descriptions</span>
            </div>
            <div style={styles.feature}>
              <span style={styles.featureIcon}>ðŸ“Š</span>
              <span style={styles.featureText}>Table & column enrichment</span>
            </div>
            <div style={styles.feature}>
              <span style={styles.featureIcon}>ðŸ”’</span>
              <span style={styles.featureText}>Secure OAuth authentication</span>
            </div>
          </div>
        </div>

        <div style={styles.footer}>
          <p style={styles.footerText}>
            Powered by Databricks Foundation Models
          </p>
        </div>
      </div>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    minHeight: '100vh',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif',
    padding: '20px',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: '16px',
    boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
    maxWidth: '450px',
    width: '100%',
    overflow: 'hidden',
  },
  header: {
    padding: '48px 32px 32px',
    textAlign: 'center',
    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    color: 'white',
  },
  logo: {
    marginBottom: '16px',
  },
  logoIcon: {
    fontSize: '48px',
    display: 'inline-block',
    animation: 'float 3s ease-in-out infinite',
  },
  title: {
    margin: '0 0 8px 0',
    fontSize: '32px',
    fontWeight: '700',
  },
  subtitle: {
    margin: 0,
    fontSize: '16px',
    opacity: 0.9,
    fontWeight: '400',
  },
  content: {
    padding: '32px',
  },
  description: {
    margin: '0 0 32px 0',
    fontSize: '15px',
    lineHeight: '1.6',
    color: '#4b5563',
    textAlign: 'center',
  },
  loginButton: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    gap: '12px',
    padding: '16px 24px',
    backgroundColor: '#667eea',
    color: 'white',
    border: 'none',
    borderRadius: '8px',
    fontSize: '16px',
    fontWeight: '600',
    cursor: 'pointer',
    transition: 'all 0.3s ease',
    boxShadow: '0 4px 12px rgba(102, 126, 234, 0.4)',
  },
  buttonIcon: {
    display: 'flex',
    alignItems: 'center',
  },
  features: {
    marginTop: '32px',
    display: 'flex',
    flexDirection: 'column',
    gap: '16px',
  },
  feature: {
    display: 'flex',
    alignItems: 'center',
    gap: '12px',
    padding: '12px 16px',
    backgroundColor: '#f9fafb',
    borderRadius: '8px',
  },
  featureIcon: {
    fontSize: '24px',
  },
  featureText: {
    fontSize: '14px',
    color: '#374151',
    fontWeight: '500',
  },
  footer: {
    padding: '24px 32px',
    backgroundColor: '#f9fafb',
    borderTop: '1px solid #e5e7eb',
    textAlign: 'center',
  },
  footerText: {
    margin: 0,
    fontSize: '13px',
    color: '#6b7280',
  },
};

// Add hover effect via CSS
const styleSheet = document.styleSheets[0];
const hoverRule = `
  button:hover {
    background-color: #5a67d8 !important;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(102, 126, 234, 0.5) !important;
  }
  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }
`;
try {
  styleSheet.insertRule(hoverRule, styleSheet.cssRules.length);
} catch (e) {}

export default Login;
